    public static String isCycle(String connects){
        if (null == connects || connects.length() < 5) return "";
        Map<String, NodePosition> positionsMap = getPositionList(connects);
        List<NodePosition> ends = positionsMap.values().stream().filter(NodePosition::isEnd).collect(Collectors.toList());
        List<NodePosition> starts = positionsMap.values().stream().filter(NodePosition::isFirst).collect(Collectors.toList());
        if (ends.size() == 0 || starts.size() == 0 ) return "开始节点或结束节点有循环依赖!";
        Map<String, List<String>> maps = new HashMap<>();
        AtomicBoolean flag = new AtomicBoolean(false);
        for (NodePosition start : starts) {
            checkCycle(positionsMap, start, maps, flag);
            if (maps.containsKey("c0")) return  maps.get("c0")+"存在循环依赖!";
        }
        return "";
    }

    private static void checkCycle(Map<String, NodePosition> positionsMap, NodePosition start, Map<String, List<String>> maps, AtomicBoolean flag){
        maps.putIfAbsent(start.getName(), new ArrayList<>());
        List<String> result = maps.get(start.getName());
        if (flag.get()) return;
        Set<String> afterDeps = start.getAfterDeps();
        if (afterDeps != null && afterDeps.size() > 0 && !flag.get()){
            for (String afterDep : afterDeps) {
                if (flag.get()) break;
                NodePosition position = positionsMap.get(afterDep);
                String line = start.getName() +"->"+ afterDep;
                if (result.contains(line)){
                    flag.set(true);
                    maps.put("c0",Collections.singletonList(line));
                    return;
                }
                result.add(line);
                checkCycle(positionsMap, position, maps, flag);
                result.remove(line);
            }
        }
    }

    public static Collection<NodePosition> calcPosition(String connects) {
        Map<String, NodePosition> positionsMap = getPositionList(connects);
        //计算深度
        calcPositionsDepth(positionsMap, null, 0);
        int max = positionsMap.values().stream().map(NodePosition::getDepth).max(Comparator.comparingInt(Integer::intValue)).get();
        System.out.println("max:" + max);

        //减少线段交点
        reduceFocusPoint(positionsMap, connects);

        //计算横向顺序及横坐标
        Map<Integer, Integer> map = calcPositionsWidth(positionsMap);
        System.out.println(map);
        int maxWidth = positionsMap.values().stream().map(NodePosition::getWidth).max(Comparator.comparingInt(Integer::intValue)).get();
        System.out.println(maxWidth);
        //居中对齐
        adjustWidth(positionsMap, map, maxWidth);


        return positionsMap.values();
    }

