    private static void reduceFocusPoint(Map<String, NodePosition> positionsMap, String connects) {
        Map<Integer, Integer> depthMaxOrderCount = new HashMap<>();
        List<NodePosition> ends = positionsMap.values().stream().filter(NodePosition::isEnd).sorted(Comparator.comparingInt(NodePosition::getDepth)).collect(Collectors.toList());
        Collections.reverse(ends);
        if (ends.size() == 0){
            System.out.println("循环依赖问题：  "+connects);
            return;
        }
        int depth = ends.get(0).getDepth();
        Map<Integer, List<String>> levelDepthMap = new HashMap<>();
        Map<Integer, List<String>> levelDepthEndMap = new HashMap<>();

        updateLevelMap(levelDepthMap, ends);
        exchangePositionNew(positionsMap, levelDepthMap, levelDepthEndMap, depth, depthMaxOrderCount);
    }

    private static void updateLevelMap(Map<Integer, List<String>> levelDepthMap, Collection<NodePosition> nodes){
        nodes.forEach(e ->{
            levelDepthMap.computeIfAbsent(e.getDepth(), k -> new ArrayList<>());
            levelDepthMap.computeIfPresent(e.getDepth(), (k,v) -> {
                if (!v.contains(e.getName())){
                    v.add(e.getName());
                }
                return v;
            });
        });
    }

    private static void exchangePositionNew(Map<String, NodePosition> positionsMap,Map<Integer, List<String>> levelDepthMap, Map<Integer, List<String>> levelDepthEndMap, int startDepth,Map<Integer, Integer> depthMaxOrderCount){
        if (startDepth < 0) return;
        List<String> list = levelDepthMap.get(startDepth);
        List<String> list1 = levelDepthEndMap.get(startDepth);
        list.addAll(list1 == null ? new ArrayList<>() : list1);
        list.forEach(n ->{
            NodePosition node = positionsMap.get(n);
            if (node.getOrder() == 0) {
                int order = depthMaxOrderCount.getOrDefault(startDepth, 0);
                order++;
                depthMaxOrderCount.put(startDepth, order);
                node.setOrder(order);
                positionsMap.put(n, node);
                Set<String> preDeps = node.getPreDeps();
                if (preDeps != null && preDeps.size() > 0) {
                    List<NodePosition> collect = preDeps.stream().map(positionsMap::get).collect(Collectors.toList());
                    List<NodePosition> l1 = collect.stream().filter(c -> c.getDepth() == startDepth - 1).collect(Collectors.toList());
                    List<NodePosition> l2 = collect.stream().filter(c -> c.getDepth() != startDepth - 1).collect(Collectors.toList());
                    updateLevelMap(levelDepthMap, l1);
                    updateLevelMap(levelDepthEndMap, l2);
                }
            }
        });
        exchangePositionNew(positionsMap,levelDepthMap,levelDepthEndMap,startDepth - 1, depthMaxOrderCount);
    }

    private static void adjustWidth(Map<String, NodePosition> positionsMap, Map<Integer, Integer> map, int maxWidth) {
        for (Integer key : map.keySet()) {
            map.put(key, (maxWidth - map.get(key)) / 2);
        }
        for (String key : positionsMap.keySet()) {
            NodePosition nodePosition = positionsMap.get(key);
            nodePosition.setWidth(nodePosition.getWidth() + map.getOrDefault(nodePosition.getDepth(), maxWidth / 2));
        }
    }

    private static Map<Integer, Integer> calcPositionsWidth(Map<String, NodePosition> positionsMap) {
        Map<Integer, Integer> map = new HashMap<>();
        positionsMap.values().forEach(n ->{
            n.setWidth(n.getOrder() * 400);
            map.putIfAbsent(n.getDepth(), 0);
            map.computeIfPresent(n.getDepth(), (k,v) ->{
                if (v < n.getWidth()){
                    return n.getWidth();
                }
                return v;
            });
        });
        return map;
    }

    private static void calcPositionsDepth(Map<String, NodePosition> positionsMap, List<String> lastDepthTasks, int depth) {
        List<String> tasks = new ArrayList<>();
        if (depth == 0) {
            for (NodePosition position : positionsMap.values()) {
                if (position.isFirst()) {
                    position.setDepth(0);
                    position.setPreLineCount(0);
                    tasks.add(position.getName());
                    position.getAfterDeps().forEach(p ->{
                        NodePosition n = positionsMap.get(p);
                        n.setPreLineCount(position.getTotalLineCount() + position.getPreLineCount());
                        n.setAdjoin(new ArrayList<>(position.getAfterDeps()));
                    });
                    Set<String> preDeps = position.getPreDeps();
                    if (preDeps != null){
                        position.setMinLineCount(1 + preDeps.stream().map(s -> positionsMap.get(s).getMinLineCount()).min(Comparator.comparingInt(Integer::intValue)).get());
                    }
                }
            }
            calcPositionsDepth(positionsMap, tasks, depth + 1);
        } else {
            depth = depth + 1;
            for (String lastDepthTask : lastDepthTasks) {
                NodePosition nodePosition = positionsMap.get(lastDepthTask);
                if (!nodePosition.isEnd()) {
                    Set<String> afterDeps = nodePosition.getAfterDeps();
                    if (null != afterDeps && afterDeps.size() > 0) {
                        depthAdd(positionsMap, afterDeps, depth - 1);
                        calcPositionsDepth(positionsMap, new ArrayList<>(afterDeps), depth);
                    }
                }
            }
        }
    }

    private static void depthAdd(Map<String, NodePosition> positionsMap, Set<String> after, int depth) {
        for (String s : after) {
            NodePosition nodePosition = positionsMap.get(s);
            nodePosition.setMinLineCount(1 + nodePosition.getPreDeps().stream().map(n -> positionsMap.get(n).getMinLineCount()).min(Comparator.comparingInt(Integer::intValue)).get());
            Set<String> afterDeps = nodePosition.getAfterDeps();
            if (afterDeps != null) {
                afterDeps.forEach(p -> {
                    NodePosition n = positionsMap.get(p);
                    n.setPreLineCount(nodePosition.getTotalLineCount() + nodePosition.getPreLineCount());
                    n.setAdjoin(new ArrayList<>(nodePosition.getAfterDeps()));
                });
            }
            if (nodePosition.getDepth() <= depth) {
                nodePosition.setDepth(depth);
            }
        }
    }

    private static Map<String, NodePosition> getPositionList(String connects) {
        JSONArray arr = JSON.parseArray(connects);
        Set<String> taskSet = new HashSet<>();
        Map<String, Set<String>> taskPreDepsMap = new HashMap<>();
        Map<String, Set<String>> taskAfterDepsMap = new HashMap<>();
        for (int i = 0; i < arr.size(); i++) {
            JSONObject json = arr.getJSONObject(i);
            String endPointSourceId = json.getString("endPointSourceId");
            String endPointTargetId = json.getString("endPointTargetId");
            taskSet.add(endPointSourceId);
            taskSet.add(endPointTargetId);
            fillMap(taskAfterDepsMap, endPointSourceId, endPointTargetId);
            fillMap(taskPreDepsMap, endPointTargetId, endPointSourceId);
        }
        Map<String, NodePosition> map = new HashMap<>();
        for (String s : taskSet) {
            NodePosition position = new NodePosition();
            position.setName(s);
            position.setPreDeps(taskPreDepsMap.get(s));
            position.setAfterDeps(taskAfterDepsMap.get(s));
            map.put(s, position);
        }
        taskPreDepsMap.clear();
        taskAfterDepsMap.clear();
        return map;
    }

    private static void fillMap(Map<String, Set<String>> map, String source, String target) {
        if (map.containsKey(source)) {
            Set<String> set = map.get(source);
            set.add(target);
        } else {
            Set<String> set = new TreeSet<>();
            set.add(target);
            map.put(source, set);
        }
    }

    public static String createGraph(Collection<NodePosition> positions, String connects) {
        JSONObject json = new JSONObject(true);
        List<JSONObject> nodes = new ArrayList<>();
        String rootName = null;
        for (NodePosition position : positions) {
            if (position.isFirst()) {
                rootName = position.getName();
            }
            JSONObject obj = new JSONObject(true);
            obj.put("id", position.getName());
            obj.put("text", position.getName());
            obj.put("order", position.getOrder());
            obj.put("depth", position.getDepth());
            obj.put("preLine", position.getTotalLineCount());
            obj.put("minPreLine", position.getMinLineCount());
            obj.put("x", position.getWidth());
            obj.put("y", position.getDepth() * 400);
            obj.put("fixed", true);
            nodes.add(obj);
        }

        JSONArray arr = JSON.parseArray(connects);
        List<JSONObject> links = new ArrayList<>();
        for (int i = 0; i < arr.size(); i++) {
            JSONObject js = arr.getJSONObject(i);
            String endPointSourceId = js.getString("endPointSourceId");
            String endPointTargetId = js.getString("endPointTargetId");
            JSONObject obj = new JSONObject(true);
            obj.put("from", endPointSourceId);
            obj.put("to", endPointTargetId);
            links.add(obj);
        }

        json.put("rootId", rootName);
        json.put("nodes", nodes);
        json.put("links", links);
        return json.toJSONString();
    }

